import {ActionType, AddPostActionCreaterType, PostType, StateType, UpdateNewPostTextActionCreaterType,} from "./state";

const ADD_POST = 'ADD-POST'
const UPDATE_NEW_POST_TEXT = 'UPDATE-NEW-POST-TEXT'

const addPostActionCreater: AddPostActionCreaterType = () => {
    return {type: ADD_POST}
}
const updateNewPostTextActionCreater: UpdateNewPostTextActionCreaterType = (text) => {
    return {type: UPDATE_NEW_POST_TEXT, newText: text}
}

export const profileReducer = (state: StateType, action: ActionType) => {
    switch (action.type) {
        case ADD_POST:
            const newPost: PostType = {
                id: new Date().getTime(),
                message: state.profilePage.newPostText,
                likesCount: 0
            };
            state.profilePage.posts.push(newPost);
            state.profilePage.newPostText = '';
            return state;
        case UPDATE_NEW_POST_TEXT:
            state.profilePage.newPostText = action.newText;
            return state;
        default:
            return state;
    }
}
