import React from "react";
import s from './Dialogs.module.css';
import DialogItem from "./DialogItem/DialogItem";
import Message from "./Message/Message";
import {DialogPageType, sendMessageActionCreator, updateNewMessageBodyActionCreator} from "../../redux/state";

type DialogsPropsType = {
    state: DialogPageType
}

export const Dialogs = (props:DialogsPropsType) => {
    
    let state = props.store.getState().dialogsPage;

    let dialogsElements = state.dialogs.map(d => <DialogItem name={d.name} id={d.id}/>);
    let messageElement = state.messages.map(m => <Message message={m.message} id={m.id}/>);
    let newMessageBody = state.newMessageBody

    let onSendMessageClick = () => {
        props.store.dispatch(sendMessageActionCreator());
}

    let onNewMessageChange = (e) => {
        let body = e.target.value;
        props.store.dispatch(updateNewMessageBodyActionCreator(body))
    }

    return (
        <div className={s.dialogs}>
            <div className={s.dialogsItems}>
                {dialogsElements}
            </div>

            <div className = {s.messages}>
                <div>{messageElement}</div>
                <div>
                    <div><textarea placeholder = "Enter a message"
                                   onChange={onNewMessageChange}
                                   value = {newMessageBody}/></div>
                    <button onClick = {onSendMessageClick}>Send</button>
                </div>
            </div>
        </div>
    )
}
