import React from "react";
import s from './MyPosts.module.css';
import Post from './Post/Post'
import {DispatchType, PostType} from "../../../redux/state";

type PropsType = {
    posts: Array<PostType>
    newPostText: string
    dispatch: DispatchType
}

const MyPosts = (props:PropsType) => {

    let postsElement =
        props.posts.map(p => <Post message={p.message} likesCount={p.likesCount}/>)

    let newPostElement = React.createRef<HTMLTextAreaElement>();

    let addPost = () => {
        props.dispatch({type: 'ADD-POST'})
        }

    const newTextChangeHandler = () => {
        let updateNewpostText = newPostElement.current?.value;
        if (updateNewpostText) {
            props.dispatch({type: 'UPFATE-NEW-POST-TEXT', newText: updateNewpostText})
        }
    }

    return (
        <div className={s.postsBlock}>
            <h3>My posts</h3>
            <div>
                <div>
                    <textarea value={props.newPostText} onChange={newTextChangeHandler}/>
                </div>
                <div>
                    <button onClick={addPost}>Add post</button>
                </div>
            </div>
            <div className={s.posts}>
                {postsElement}
            </div>
        </div>
    )
}


export default MyPosts;
