import React from "react";
import {followAC, setUsersAC, unfollowAC, UsersPropsType} from "../../redux/users-reducer";
import {connect} from "react-redux";
import {Users} from "./Users";
import {ActionType, StateType} from "../../redux/state";


type MapStateToProps = {
    users: []
    //usersPage: UsersPropsType
}
type MapDispatchToProps = {
    follow: (userId: number) => void
    unfollow: (userId: number) => void
    setUsers: (users: []) => void
}

let mapStateToProps = (state: StateType): MapStateToProps => {
    return {
        users: state.usersPage.users
    }
}

let mapDispatchToProps = (dispatch: (action: ActionType) => void): MapDispatchToProps => {
    return {
        follow: (userId) => {
            dispatch(followAC(userId))
        },
        unfollow: (userId) => {
            dispatch(unfollowAC(userId));
        },
        setUsers: (users) => {
            dispatch(setUsersAC(users))
        }
    }
}

export default connect<MapStateToProps, MapDispatchToProps, {}, StateType>
(mapStateToProps, mapDispatchToProps) (Users);
