import {
    ActionType,
    PostType,
    SendMessageActionCreatorType,
    StateType,
    UpdateNewMessageBodyActionCreatorType
} from "./state";

const UPDATE_NEW_MESSAGE_BODY = 'UPDATE-NEW-MESSAGE-BODY'
const SEND_MESSAGE = 'SEND-MESSAGE'

const sendMessageActionCreator: SendMessageActionCreatorType = () => {
    return {type: SEND_MESSAGE}
}
const updateNewMessageBodyActionCreator: UpdateNewMessageBodyActionCreatorType = (body) => {
    return {type: UPDATE_NEW_MESSAGE_BODY, body: body}
}

export const gialogReducer = (state: StateType, action: ActionType) => {
    switch (action.type) {
        case UPDATE_NEW_MESSAGE_BODY:
            state.dialogsPage.newMessageBody = action.body;
            return state;
        case SEND_MESSAGE:
            let body = state.dialogsPage.newMessageBody;
            state.dialogsPage.newMessageBody = '';
            state.dialogsPage.messages.push({id: 6, message: body});
            return state;
        default:
            return state;
    }
}
